package org.neo4j.kernel.impl.ha;

/**
 * Represents a result of a committed transaction, either it was
 * {@link TransactionStatus#SUCCESSFUL} and the {@code txId} generated by the
 * master is passed with it. Or it {@link TransactionStatus#FAILED} and then
 * the {@code txId} can be ignored.
 */
public class TransactionResult
{
    private final TransactionStatus status;
    private final long txId;

    public TransactionResult( TransactionStatus status, long txId )
    {
        this.status = status;
        this.txId = txId;
    }

    public TransactionStatus getStatus()
    {
        return status;
    }

    public long getTxId()
    {
        if ( status != TransactionStatus.SUCCESSFUL )
        {
            throw new RuntimeException( "The transaction wasn't successful," +
            		" so the txId doesn't represent anything useful" );
        }
        return txId;
    }
}
